import React, {Component} from 'react';
import Axios from 'axios';


class LogIn extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''

        }
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        // this.handleClearForm = this.handleClearForm.bind(this);
    }

    /* This life cycle hook gets executed when the component mounts */
    handleChange(e) {
        this.setState({
            email: e.target.value,
            password: e.target.value
        });
    }
    handleFormSubmit(e) {
        // Form submission logic
        e.preventDefault();
        const userDetails = {email: this.state.email, password: this.state.password};
        Axios.post("http://localhost:8080/login", userDetails)
            .then( (res) => {
                console.log(res.json());
            }).catch( (error) => {
            console.log(error);
        });
    }
    // handleClearForm(e) {
    //     // Logic for resetting the form
    //     e.preventDefault();
    //     this.setState({email: "", password: ""});
    //
    // }
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <p className="title">Flights</p>
                <p className="subtitle">Get flights by entering departure city iata and arrival city iata</p>
                <div className="field">
                    <label className="label">Depature_iata</label>
                    <div className="control">
                        <input
                            className="input"
                            type="text"
                            placeholder="Type departure iata name here"
                            onChange={e => this.updateFlight(e)} />
                    </div>
                </div>
                <div className="field">
                    <label className="label">Arrival_iata</label>
                    <div className="control">
                        <input
                            className="input"
                            type="text"
                            placeholder="Type arrival iata name here"
                            onChange={e => this.updateFlight(e)} />
                    </div>
                </div>

                <div className="field">
                    <div className="control">
                        <input
                            type='submit'
                            value='Search' />
                    </div>
                </div>
            </form>
            // <form onClick={this.handleFormSubmit}>
            //     <input type="text" id="email" placeholder="email address" name="email" onChange={e => this.handleChange(e)} />
            //     <input type="text" id="password" placeholder="password" name="password" onChange={e => this.handleChange(e)}/>
            //     <input type="submit" id="submit" name="submit" value="submit" />
            // </form>
        );
    }
}

export default LogIn;
