import React, {Component} from 'react';
import Axios from 'axios';

class LogIn extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''

        }
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        // this.handleClearForm = this.handleClearForm.bind(this);
    }

    /* This life cycle hook gets executed when the component mounts */
    handleChange(e) {
        this.setState({
            email: e.target.value,
            password: e.target.value
        });
    }
    handleFormSubmit(e) {
        // Form submission logic
        e.preventDefault();
        const userDetails = {email: this.state.email, password: this.state.password};
        Axios.post("http://localhost:8080/login", userDetails)
            .then( (res) => {
                console.log(res);
            }).catch( (error) => {
            console.log(error);
        });
    }
    // handleClearForm(e) {
    //     // Logic for resetting the form
    //     e.preventDefault();
    //     this.setState({email: "", password: ""});
    //
    // }
    render() {
        return (
            <form onClick={this.handleFormSubmit}>
                <input type="text" placeholder="email address" onChange={e => this.handleChange(e)} />
                <input type="text" placeholder="password"  onChange={e => this.handleChange(e)}/>
                <input type="submit" value="submit" />
            </form>
        );
    }
}

export default LogIn;
