import React, {Component} from 'react';
import Axios from 'axios';
/* Import Components */
// import CheckBox from '../components/CheckBox';
// import Input from '../components/Input';
// import TextArea from '../components/TextArea';
// import Select from '../components/Select';
// import Button from '../components/Button'

class LogIn extends Component {
    constructor(props) {
        super(props);

        this.state = {
            newUser: {
                email: '',
                password: ''

            }

        }
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.handleClearForm = this.handleClearForm.bind(this);
    }

    /* This life cycle hook gets executed when the component mounts */

    handleFormSubmit(e) {
        // Form submission logic
        e.preventDefault();
        this.setState({
            email: e.target.value,
            password: e.target.value
        });
        const userDetails = {email: this.state.email, password: this.state.password}
        console.log(this.state.email);
        Axios.post("http://localhost:8080/login", userDetails)
            .then( (res) => {
                console.log(res);
            }).catch( (error) => {
            console.log(error);
        })
    }
    handleClearForm(e) {
        // Logic for resetting the form
        e.preventDefault();
        this.setState({email: "", password: ""});

    }
    render() {
        return (
            <form className="container" onSubmit={this.handleFormSubmit}>
                <input type="text" id="email" placeholder="" name="email" required />
                <input type="text" id="password" placeholder="" name="password" required />
                <input type="submit" id="submit"
                       name="submit" value="submit" />
                <input type="submit" id="reset" name="reset" value="reset" onClick={this.handleClearForm}/>
            </form>
        );
    }
}

export default LogIn;
