import React from "react";
import {Table} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const FS = () => {
    // const[airportsData, setAirportsData] = React.useState([])
    const[userData, setUserData] = React.useState([])
    const[userSearchData, setUserSearchData] = React.useState([]);
    const[country_code, setCountryCode] = React.useState('');
    const[status, setStatus] = React.useState('');

    React.useEffect( () => {
        axios({method: "GET", url: "/airportsDB"})
            .then(
                res => {
                    // console.log(res)
                    const airportsData = res.data["data"];
                    setUserData(airportsData)
                    setUserSearchData(airportsData)

                    // console.log(airportsData)
                }
            )
        console.log("Meow")
        console.log(userData)
        console.log(userSearchData)
    }, [])
    // React.useEffect(()=>{
    //     // const data = Axios.get("/AirlinesDB");
    //     const data = [
    //         {country: "France", city: "Paris", airport: "Paris Charles de Gaulle Airport", status: "Safe"},
    //         {country: "Japan", city: "Tokyo", airport: "Haneda Airport", status: "Safe"},
    //         {country: "Japan", city: "Tokyo", airport: "Narita International Airport", status: "Safe"},
    //         {country: "Germany", city: "Berlin", airport: "Berlin Brandenburg Airport", status: "Safe"},
    //     ];
    //
    //     setUserData(data);
    //     setUserSearchData(data);
    // },[])

    const handleSearch = () => {
        const newData =
            userData
                .filter(x => x.country_code === (country_code === '' ? x.country_code : country_code))
                .filter(y => y.status === (status === '' ? y.status : status))
        setUserSearchData(newData)

    }

    // const handleGetData = () => {
    //     // making a request to backend for flight search.
    //     axios({method: "GET", url: "/airportsDB"})
    //         .then(
    //             res => {
    //                 // console.log(res)
    //                 const airportsData = res;
    //                 setUserData(airportsData)
    //                 setUserSearchData(airportsData)
    //
    //                 // console.log(airportsData)
    //             }
    //         )
    //
    // }

    return (
        <div>
            <Table>
                <tr>
                    <td>
                        <input class="form-control" type='text' placeholder="Enter country..." onChange={(e) => setCountryCode(e.target.value)}/>
                    </td>
                    <td>
                        <select className="form-control" onChange={(e)=> setStatus(e.target.value)}>
                            <option value=''>-Select-</option>
                            <option value='Safe'>Safe</option>
                            <option value='Unsafe'>Unsafe</option>
                        </select>
                    </td>
                    <td>
                        <button type="button" class="btn btn-outline-primary" onClick={()=> handleSearch()}>Search</button>
                        {/*<button type="button" onClick={() => handleGetData()}>Get Airports DB!</button>*/}
                    </td>
                </tr>
            </Table>

            <Table responsive stripped size="sm">
                <thead>
                <tr>
                    <th>Country</th>
                    <th>City</th>
                    <th>Airport</th>
                    <th>Status</th>
                </tr>
                </thead>
                <tbody>
                {
                    userSearchData && userSearchData.length >0 ?
                        userSearchData.map(item =>
                            <tr>
                                <td>{item.country_code}</td>
                                <td>{item.name}</td>
                                {/*<td>{item.status}</td>*/}
                            </tr>
                        )
                        : 'No data meow'
                }
                </tbody>
            </Table>
            <h1>This page should only appear once the user is logged in</h1>
        </div>
    );
};

export default FS;
